#include<stdio.h>
int main(){
    int x;
    printf("Enter the value of x \n");
    scanf("%d",&x);
    switch(x % 2){
        case 0:
            printf("%d is an even number\n",x);
        break;
       
        case 0:
         printf("%d is an odd number \n",x);
        break;

    }
    return 0;
}